css:
@font-face {
    font-family: Acme;
    font-style: normal;
    font-weight: 400;
    font-display: swap;
    src: local('Acme Regular'), local('Acme-Regular'), url('data:font/woff2;base64,') format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD
}

@font-face {
    font-family: DOCK11_Heavy;
    font-style: normal;
    font-weight: 400;
    src: local('DOCK11 Heavy'), url('data:font/woff;base64,') format('woff')
}

html {
    overflow: visible;
    user-select: none
}

*, .ui-helper-reset {
    font-size: 100%;
    padding: 0;
    scrollbar-width: none;
}

.bounce, .slip-in, .slip-out {
    animation-timing-function: ease;
    animation-iteration-count: 1
}

#font1, #font2, #font3, #font4, #frame, #help div, #keys div, #record {
    display: none
}

#fileInputForm p, #record {
    width: 95%
}

#imgload, #record, body, input, select, textarea {
    color: #444
}

#preview, .minimize, .select-lang, input, input[type=checkbox] {
    vertical-align: middle
}

#code pre.code-bottom, #code pre.code-top, #footer-container {
    padding-left: 15px
}

.codeTab, .previewTab, hr {
    border-image: none 100%/1/0 stretch
}

#main, .menu_icon, .modal-open, .presets .patterns li a {
    overflow: hidden
}

.clearfix::after, .flash, .sp-cf::after {
    clear: both
}

canvas, img {
    image-rendering: optimizeQuality;
    image-rendering: -moz-crisp-edges;
    image-rendering: -webkit-optimize-contrast;
    image-rendering: optimize-contrast
}

.ui-helper-reset {
    margin: 0;
    border: 0;
    outline: currentcolor 0;
    line-height: 1.3;
    text-decoration: none;
    list-style: none
}

.ui-widget-content, .ui-widget-header {
    border: 1px solid #b6b6b6;
    color: #4f4f4f
}

.ui-helper-clearfix::after {
    content: ".";
    display: block;
    height: 0;
    clear: both;
    visibility: hidden
}

.ui-helper-clearfix {
    display: block
}

#send, .lds-spinner, .menu_icon {
    display: inline-block
}

.ui-state-disabled {
    cursor: default !important
}

.menu_icon, .ui-tabs .ui-tabs-nav li a, button, input[type=button], label {
    cursor: pointer
}

.ui-widget {
    font: 1.1em Helvetica, Arial, sans-serif
}

.ui-widget-content {
    background: #fff
}

.ui-widget-content a {
    color: #4f4f4f
}

.ui-widget-header {
    font-weight: 700;
    background: linear-gradient(to top, #c4c4c4 0, #ededed 100%) rgba(0, 0, 0, 0)
}

.ui-widget-header a {
    color: #4f4f4f
}

.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
    border: 1px solid #b6b6b6;
    font-weight: 400;
    color: #4f4f4f;
    background: linear-gradient(to top, #c4c4c4 0, #ededed 100%) rgba(0, 0, 0, 0);
    box-shadow: rgba(255, 255, 255, .6) 0 1px 0 inset
}

.ui-state-focus, .ui-state-hover, .ui-widget-content .ui-state-focus, .ui-widget-content .ui-state-hover, .ui-widget-header .ui-state-focus, .ui-widget-header .ui-state-hover {
    border: 1px solid #9d9d9d;
    font-weight: 400;
    color: #313131
}

.ui-state-active, .ui-widget-content .ui-state-active, .ui-widget-header .ui-state-active {
    outline: currentcolor 0;
    color: #1c4257;
    border: 1px solid #7096ab;
    background: linear-gradient(to top, #92bdd6 0, #b9e0f5 100%) rgba(0, 0, 0, 0);
    box-shadow: none
}

.ui-state-active a, .ui-state-active a:link, .ui-state-active a:visited {
    color: #313131;
    text-decoration: none
}

.ui-widget :active {
    outline: currentcolor 0
}

.ui-corner-top {
    border-top-right-radius: 3px
}

.ui-corner-bottom {
    border-bottom-right-radius: 3px
}

.ui-corner-top {
    border-top-left-radius: 3px
}

.ui-corner-bottom {
    border-bottom-left-radius: 3px
}

.ui-corner-all {
    border-radius: 3px
}

.lds-spinner, .ui-slider, .ui-tabs {
    position: relative
}

.ui-slider {
    text-align: left;
    background: #d7d7d7;
    box-shadow: rgba(0, 0, 0, .5) 0 1px 2px inset
}

.ui-slider .ui-slider-handle {
    background: url("data:image/png;base64, ") 0 -23px no-repeat rgba(0, 0, 0, 0);
    position: absolute;
    z-index: 2;
    width: 23px;
    height: 23px;
    cursor: default;
    border: none;
    outline: currentcolor 0;
    box-shadow: none
}

#loading {
    height: 100%;
    top: 0
}

.menu_icon, .ui-tabs .ui-tabs-nav {
    background: rgba(0, 0, 0, 0);
    margin: 0
}

.ui-slider-horizontal {
    height: 5px
}

.ui-slider-horizontal .ui-slider-handle {
    top: -8px;
    margin-left: -13px
}

.ui-tabs {
    transform-origin: 0px 0px 0px;
    border: 0
}

.ui-tabs .ui-tabs-nav {
    padding: 0;
    border-width: 0 0 1px;
    border-radius: 0
}

.ui-tabs .ui-tabs-nav li {
    list-style: none;
    float: left;
    position: relative;
    top: 1px;
    margin: 0 .2em 1px 0;
    border-bottom: 0 !important;
    padding: 0;
    white-space: nowrap
}

.ui-tabs .ui-tabs-nav li a {
    float: left;
    padding: .5em 1em;
    text-decoration: none;
    font-size: 12px;
    font-weight: 700;
    text-shadow: rgba(255, 255, 255, .5) 0 1px 0
}

.ui-tabs .ui-tabs-nav li.ui-tabs-selected {
    margin-bottom: 0;
    padding-bottom: 1px;
    background: #fff;
    border-color: #b6b6b6
}

.ui-tabs .ui-tabs-nav li.ui-tabs-selected a {
    cursor: text;
    outline: currentcolor 0
}

.ui-tabs .ui-tabs-panel {
    display: block;
    border-width: 0 1px 1px;
    padding: .5em 1.4em;
    background: rgba(0, 0, 0, 0);
    border-radius: 0
}

.ui-tabs .ui-tabs-hide {
    display: none !important
}

input:not([type=checkbox]):not([type=radio]):not([type=file]), textarea {
    padding: 3px;
    border-radius: 2px;
    border: 1px solid #cecece;
    outline: currentcolor 0;
    box-shadow: rgba(0, 0, 0, .1) 0 1px 3px inset, rgba(255, 255, 255, .2) 0 1px 0;
    transition: .25s ease-in-out
}

input, textarea {
    box-shadow: rgba(255, 255, 255, .8) 0 1px 0, rgba(0, 0, 0, .15) 0 1px 4px inset
}

input:not([type=checkbox]):not([type=radio]):not([type=file]):hover, textarea:hover {
    border: 1px solid #bdbdbd;
    box-shadow: rgba(0, 0, 0, .2) 0 1px 3px inset, rgba(255, 255, 255, .2) 0 1px 0
}

input:not([type=checkbox]):not([type=radio]):not([type=file]):focus, textarea:focus {
    border: 1px solid #95bdd4;
    box-shadow: rgba(161, 202, 226, .5) 0 2px 3px inset, rgba(255, 255, 255, .2) 0 1px 0
}

#sizeform input, * {
    vertical-align: baseline
}

*, button, hr, input, select, textarea {
    margin: 0
}

.menu_icon:focus, a:focus {
    outline: currentcolor 0
}

#editcontent, #frame, #loading, .fixed-message, body {
    place-items: center
}

* {
    border: 0
}

footer, header {
    display: block;
    background: url("data:image/png;base64, iVBORw0KGgoAAAANSUhEUgAAAAMAAAAbCAYAAAC5rTVJAAAAJElEQVQYlWP4DwVPnz79zwBj/P///z8DjPH//3+IzKgykpUBAKX6OpJ/kHykAAAAAElFTkSuQmCC") rgba(0, 0, 0, 0)
}

hr {
    border-color: currentcolor currentcolor #fff;
    border-style: none none solid;
    border-width: medium medium 1px;
    background-color: #cbcbcb;
    height: 1px
}

#help hr {
    border: none;
    background-color: #f45c2d;
    height: 1px;
    margin: 5px 0 15px
}

select {
    vertical-align: top
}

body {
    font: 16px/1.231 Acme, Verdana;
    display: grid
}

.lds-spinner {
    width: 80px;
    height: 80px
}

.lds-spinner div {
    transform-origin: 40px 40px 0px;
    animation: 1.2s linear infinite lds-spinner
}

.lds-spinner div::after {
    content: " ";
    display: block;
    position: absolute;
    top: 3px;
    left: 37px;
    width: 6px;
    height: 18px;
    border-radius: 20%;
    background: #fff
}

#loading, body {
    background: #f63
}

.lds-spinner div:nth-child(1) {
    transform: rotate(0);
    animation-delay: -1.1s
}

.lds-spinner div:nth-child(2) {
    transform: rotate(30deg);
    animation-delay: -1s
}

.lds-spinner div:nth-child(3) {
    transform: rotate(60deg);
    animation-delay: -.9s
}

.lds-spinner div:nth-child(4) {
    transform: rotate(90deg);
    animation-delay: -.8s
}

.lds-spinner div:nth-child(5) {
    transform: rotate(120deg);
    animation-delay: -.7s
}

.lds-spinner div:nth-child(6) {
    transform: rotate(150deg);
    animation-delay: -.6s
}

.lds-spinner div:nth-child(7) {
    transform: rotate(180deg);
    animation-delay: -.5s
}

.lds-spinner div:nth-child(8) {
    transform: rotate(210deg);
    animation-delay: -.4s
}

.lds-spinner div:nth-child(9) {
    transform: rotate(240deg);
    animation-delay: -.3s
}

.lds-spinner div:nth-child(10) {
    transform: rotate(270deg);
    animation-delay: -.2s
}

.lds-spinner div:nth-child(11) {
    transform: rotate(300deg);
    animation-delay: -.1s
}

.lds-spinner div:nth-child(12) {
    transform: rotate(330deg);
    animation-delay: 0s
}

@keyframes lds-spinner {
    0% {
        opacity: 1
    }

    100% {
        opacity: 0
    }
}

@keyframes pulse {
    0% {
        background-color: #ffb
    }

    50% {
        background-color: #3f3
    }

    100% {
        background-color: #ffb
    }
}

code, pre {
    font-family: monospace, sans-serif;
    font-size: smaller;
    font-weight: 700
}

img, pre {
    break-inside: avoid
}

ol, ul {
    margin-left: 2em
}

ol {
    list-style-type: decimal
}

pre {
    padding: 15px;
    white-space: pre-wrap;
    overflow-wrap: break-word
}

textarea {
    overflow: auto;
    width: 90%;
    height: 160px
}

input[type=radio] {
    transform: translateY(-3px)
}

button {
    width: auto;
    overflow: visible
}

h1, h2, h3, h4, h5, h6 {
    font-weight: 700
}

h2, h3 {
    break-after: avoid
}

a, a:active, a:visited {
    color: #f45c2d;
    text-decoration: none;
    font-weight: 700
}

a:hover {
    color: #000
}

@page {
    margin: .5cm
}

#loading {
    display: grid;
    position: fixed;
    width: 100%;
    font-size: 8em;
    text-transform: uppercase;
    font-family: Dock11_Heavy;
    text-shadow: #123 5px 5px 10px;
    font-weight: 300;
    z-index: 10000
}

#fileInputForm, #fileInputForm input[type=button], #fileInputForm p, #reccounter, #record, #sizeform input, #sizeform input[type=text], .keys, .menu_icon, .minimize, .recloader, .tooltip, h1, header, header h2, input[type=checkbox]+label {
    position: relative
}

.list td:first-child, header h2 {
    font-weight: 700;
    text-align: center
}

h1 {
    top: -61px
}

h2 {
    font-size: 24px
}

header h2 {
    margin-top: -70px;
    font-size: 28px
}

h3 {
    font-size: 20px;
    margin-bottom: 8px
}

#help h3 {
    text-decoration-line: underline
}

.col2 h4, .draw-options h4, .text-control h4 {
    transform: translateX(-10px);
    width: 109%
}

.draw-options {
    font-size: 12px
}

#fileInputForm h4, #recInputForm, .modal h4, table h4 {
    font-size: 12px;
    text-align: center;
    padding-top: 15px
}

#help, #record h4, header {
    padding: 10px
}

p {
    margin: 5px;
    padding-left: 5px
}

.menu_icon {
    border: none;
    height: 40px;
    left: 10px;
    padding: 0;
    top: -8px;
    transition: .3s;
    width: 24px
}

.menu_icon span, .menu_icon span::after, .menu_icon span::before {
    position: absolute;
    display: block;
    left: 0;
    height: 3px;
    background: #444
}

.menu_icon span {
    border-radius: 3px;
    right: 0;
    top: 18px
}

.menu_icon span::after, .menu_icon span::before {
    width: 100%;
    border-radius: 2px;
    content: ""
}

.menu_icon span::before {
    top: -6px
}

.menu_icon span::after {
    bottom: -6px
}

.menu_icon-X span {
    transition: 0s .3s
}

.menu_icon-X span::after, .menu_icon-X span::before {
    transition-duration: .3s, .3s;
    transition-delay: 0.3s, 0s
}

.menu_icon-X span::before {
    transition-property: top, transform
}

.menu_icon-X span::after {
    transition-property: bottom, transform
}

.menu_icon-X.is-active span {
    background: rgba(0, 0, 0, 0)
}

.menu_icon-X.is-active span::before {
    top: 0;
    transform: rotate(45deg)
}

.menu_icon-X.is-active span::after {
    bottom: 0;
    transform: rotate(-45deg)
}

.menu_icon-X.is-active span::after, .menu_icon-X.is-active span::before {
    transition-delay: 0s, 0.3s
}

#frame {
    margin: 52px auto 0;
    cursor: default
}

#container {
    background-color: #fff;
    border-radius: 40px
}

header {
    border-radius: 40px 40px 0 0;
    height: 20px;
    box-shadow: rgba(0, 0, 0, .15) 0 1px 5px, #fff 0 1px 0 inset
}

.listening, .minimize {
    padding: 3px;
    cursor: pointer
}

.h1 {
    margin: 0 auto;
    height: 65px;
    width: 470px;
    transform-origin: center bottom 0px;
    background: url("data:image/png;base64, ") no-repeat rgba(0, 0, 0, 0)
}

.mini_max, .mini_min {
    background-size: 24px;
    background-repeat: no-repeat;
    background-position: center center
}

.bounce {
    animation-name: bounce;
    animation-duration: 1.5s
}

.slip-in, .slip-out {
    animation-duration: .5s
}

.slip-in {
    animation-name: slip-in
}

.slip-out {
    animation-name: slip-out
}

@keyframes bounce {
    0%, 100%, 80% {
        transform: scale(1) translateY(0)
    }

    25% {
        transform: scale(1.05, .8) translateY(4px)
    }

    40% {
        transform: scale(.7, 1.1) translateY(-20px)
    }

    55% {
        transform: scale(1, .9) translateY(3px)
    }

    70% {
        transform: scale(1, 1.1) translateY(-4px)
    }
}

@keyframes slip-in {
    0% {
        transform: scale(.01) translate(-50%, -50%)
    }

    100% {
        transform: scale(1) translate(0, 0)
    }
}

@keyframes slip-out {
    0% {
        transform: scale(1) translate(0, 0)
    }

    100% {
        transform: scale(.01) translate(-50%, -50%)
    }
}

.minimize {
    float: right;
    top: 6px;
    right: 25px;
