}

.mini_min {
    background-image: url("data:image/png;base64, iVBORw0KGgoAAAANSUhEUgAAABgAAAAYCAYAAADgdz34AAAANUlEQVRIie3MIQ4AIBADwb3/vxdz5jAEBAaBIGQnaapakCTpN0lQQAJt9FmCmtv1sanL0SM6o/QniKjXk1QAAAAASUVORK5CYII=")
}

.listening {
