    bottom: 0;
    left: 0
}

#grid .cursors li, #grid .cursors li a {
    position: relative;
    display: block
}

#grid .cursors li a {
    background: #e0e5e8;
    text-align: center;
    color: #606261;
    font-size: 10px;
    cursor: pointer
}

#grid .cursors li a:hover, #grid .cursors li.selected a {
    background: #f45c2d;
    color: #fff
}

#grid .cursors li a:hover {
    transform: scale(1.3)
}

#grid .cursors li a::after {
    display: block;
    content: "";
    height: 0;
    width: 0
}

#grid .cols li a, #grid .rows li a {
    width: 14px;
    height: 14px;
    line-height: 14px
}

#grid .cols {
    top: 10px
}

#grid .cols li a {
    left: -7px
}

#grid .cols li a::after {
    border-color: #e0e5e8 transparent transparent;
    border-style: solid;
    border-width: 7px
}

#grid .cols li a:hover::after, #grid .cols li.selected a::after {
    border-color: #f45c2d transparent transparent
}

#grid .rows {
    left: 10px
}

#grid .rows li a {
    top: -7px
}

#grid .rows li a::after {
    border-color: transparent transparent transparent #e0e5e8;
    border-style: solid;
    border-width: 7px;
    position: absolute;
    left: 14px;
    top: 0
}

#grid .rows li a:hover::after, #grid .rows li.selected a::after {
    border-color: transparent transparent transparent #f45c2d
}

