.text-area .font {
    font-family: sans-serif;
    font-size: 12px
}

.modal-wrapper {
    align-items: center;
    bottom: 0;
    right: 0;
    z-index: 2147483647;
    padding: 60px;
    overflow-y: auto
}

.modal-overlay {
    width: 100%;
    height: 100%;
    z-index: 2147483647;
    background: rgba(0, 0, 0, .6)
}

.modal {
    min-width: 0;
    display: grid;
    align-items: center
}

.online-session {
    display: none;
    position: absolute;
    left: 30px;
    top: 4px;
    font-size: 10px;
    padding: 2px
}

.copy-items {
    position: absolute;
    top: 50%;
    left: 50%;
    background: #eee;
    border: 3px solid #999;
    border-radius: 10px;
    padding: 0 10px 15px;
    margin: 0;
    z-index: 1
}

#reccontent, .canvas-control {
    position: relative;
    white-space: nowrap
}

#reccontent, .control-canvas canvas {
    margin: 0 auto
}

.control-canvas {
    display: grid;
    align-items: center;
    background-color: #fff;
    padding: 10px
}

.canvas-control {
    background-color: #ccc;
    bottom: -15px
}

#editcontent, #rec_overview {
    position: fixed;
    z-index: 2
}

#reccontent {
    z-index: 1;
    font-family: Acme;
    font-size: 22px;
    color: #bbb;
    background: #222;
    border: 3px solid #999;
    border-radius: 20px;
    text-align: center;
    max-width: max-content
}

.big, .editsublist {
    white-space: normal
}

#rec_overview {
    bottom: 0;
    left: 50%;
    width: 900px;
    min-height: 150px;
    color: #eee;
    background-color: rgba(0, 0, 0, .85);
    border: 3px solid #eee;
    border-radius: 30px 30px 0 0;
    transform: translate(-50%)
}

.clickable {
    padding: 10px
}

.box, .editbutton, form[name=addform] td:nth-child(3n+1), form[name=editform] td:nth-child(3n+1) {
    font-size: 16px
}

.clickable:hover {
    border-radius: 10px;
    transition: 50ms;
    height: 25px;
    background-color: rgba(244, 92, 45, .7)
}

#editcontent {
    margin: 0;
    width: 80%;
    max-width: 1000px;
    height: 80%;
    top: 10%;
    left: 10%;
    background-color: #111;
    border-radius: 30px 0 0
}

.editsublist {
    z-index: 40;
    padding: 0 20px
}

.editsublist h4 {
    text-align: center
}

.addtime, .content, .editcontent, .edittime {
    font-family: monospace;
    display: inline-block;
    padding: 0;
    margin: 0;
    line-height: 24px;
    background: inherit;
    vertical-align: baseline;
    min-width: 25px;
    max-width: 600px;
    user-select: auto;
    cursor: pointer;
    outline: currentcolor 0
}

.session-message {
    display: grid !important;
    grid-template-columns: 1fr 5fr;
    grid-template-rows: 20px 20px 20px 20px 20px;
    gap: 0 0;
    grid-template-areas: ". ." ". ." ". ." ". ." ". .";
    height: 100px !important;
    font-size: 14px !important;
    font-family: Consolas !important
}

.sp-cancel, .sp-container button, .sp-replacer, form[name=addform] td, form[name=editform] td {
    vertical-align: middle
}

sp-palette-toggle {
    font-weight: 700
}

.big, .wrap {
    max-width: 550px
}

.small {
    min-width: 10px
}

.big {
    min-width: 100px;
    min-height: 50px;
    max-height: 450px;
    overflow-y: scroll
}

.noborder {
    border: none
}

.border, .border_small {
    border: 1px solid #aaa
}

.border {
    padding: 20px;
    background: rgba(0, 0, 0, .75)
}

.border_small {
    border-radius: 4px
}

.line_small {
    border-bottom: 1px solid #fff;
    margin-right: 10px;
    min-width: 30px
}

.empty {
    min-width: 30px;
    margin: 0 8px;
    line-height: 20px
}

.round-edges {
    border-radius: 10px
}

.editbutton {
    padding: 8px 10px;
    margin-top: 20px;
    color: #000;
    background: url("data:image/png;base64, iVBORw0KGgoAAAANSUhEUgAAABsAAAAbCAIAAAACtmMCAAAATUlEQVR42mMsqSiwtLB6++6NsJDI8RPH1NTUKGQwrt2wiorGARmMs+ZNo6JxQAbIjVQ0DuRrYDhS0bhRX4/6etTXo74e9fWor0d9fQwAFWjv816zpSEAAAAASUVORK5CYII=") rgba(0, 0, 0, 0);
    width: 130px;
    height: 35px
}

.editbutton:hover {
    color: #fff;
    background: #292 !important
}

.nowrap {
    white-space: nowrap
}

.wrap {
    white-space: normal;
    word-break: break-word
}

.col1.center, input.center {
    position: absolute;
    left: 50%;
    transform: translate(-50%);
    text-align: center
}

.right {
    text-align: right
}

.head, .message {
    text-align: center
}

.off {
    opacity: .3;
    pointer-events: none
}

.no_pointer_events {
    cursor: default
}

.padding15 {
    padding: 0 15px
}

.head {
    width: 220px;
    margin: 0 auto 40px;
    border-bottom: 1px solid #eee
}

.box {
    width: 250px;
    height: 25px;
    border: 1px solid #999;
    color: #eee;
    background-color: #333;
    border-radius: 5px;
    box-shadow: #111 4px 4px
}

.funstuff {
    display: flex;
    flex-flow: row nowrap;
    place-content: center;
    align-items: center;
    background: #f63
}

.sp-alpha-enabled .sp-alpha {
    display: block
}

.funstuff div {
    position: relative;
    width: 100px;
    height: 100px;
    margin: 10px 25px
}

.disappear {
    opacity: 0;
    transition: 3s
}

.opened_eye {
    background-image: url("data:image/svg+xml,%3csvg width='100' height='100' viewBox='0 0 100 100' xmlns='http://www.w3.org/2000/svg' xmlns:svg='http://www.w3.org/2000/svg'%3e %3cg%3e %3ccircle cx='50' cy='50' fill='white' fill-opacity='1' r='46' stroke='black' stroke-width='4'/%3e %3c/g%3e %3c/svg%3e");
    z-index: 10
}

.halfopened_eye {
    background-image: url('data:image/png;base64,')
}

