.alert-backdrop *, .alert-toast-container * {
    padding: 0;
    font-family: Montserrat, sans-serif;
    margin: 0;
    user-select: none
}

.alert-glass .alert-glassmorphism::after, .alert-glass .alert-glassmorphism::before {
    content: "";
    position: absolute;
    height: 100%;
    width: 100%;
    top: 0;
    left: 0
}

.alert-backdrop .alert-container.alert-glass .alert-close, .alert-glass {
    color: #fff !important
}

.alert-backdrop .alert-container .alert-buttons-container button:hover, .alert-backdrop .alert-container .alert-close:active {
    transform: scale(.95)
}

.alert-backdrop *, .alert-toast-container * {
    box-sizing: border-box;
    outline: currentcolor 0
}

.alert-backdrop ::-webkit-scrollbar {
    display: none
}

.alert-hide {
    display: none !important
}

.alert-glass {
    background: rgba(255, 255, 255, .1) !important
}

.alert-glass .alert-glassmorphism {
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    background-color: #000;
    z-index: -1
}

.alert-glass .alert-glassmorphism::before {
    background: linear-gradient(45deg, red, #00f) rgba(0, 0, 0, 0);
    clip-path: circle(40% at right 80%)
}

.alert-glass .alert-glassmorphism::after {
    background: linear-gradient(45deg, #1fe6ff, #673ab7) rgba(0, 0, 0, 0);
    clip-path: circle(40% at left 25%)
}

.alert-backdrop .alert-container.alert-glass .alert-input {
    background-color: rgba(255, 255, 255, .2) !important;
    border-color: rgba(255, 255, 255, .5) !important;
    color: #fff !important
}

.alert-backdrop .alert-container.alert-glass .alert-footer {
    border-color: rgba(255, 255, 255, .2) !important
}

.alert-glass .alert-time-line {
    background: rgba(255, 255, 255, .2) !important
}

.alert-backdrop .alert-container.alert-glass .alert-buttons-container button {
    background-color: rgba(255, 255, 255, .1) !important;
    border-color: rgba(255, 255, 255, .2) !important
}

.alert-backdrop .alert-container.alert-glass .alert-buttons-container button:active {
    box-shadow: rgba(255, 255, 255, .01) -2px -2px 6px inset, rgba(0, 0, 0, .8) 2px 2px 6px inset
}

.alert-dark-neumorphism {
    background: #131419 !important
}

.alert-dark-neumorphism * {
    color: #03a9f4 !important
}

.alert-backdrop {
    display: flex;
    align-items: center;
    min-height: 100vh;
    top: 0;
    left: 0;
    height: 100vh;
    background: rgba(0, 0, 0, .5);
    overflow: hidden
}

.alert-top-left {
    justify-content: flex-start !important;
    align-items: flex-start !important
}

.alert-top {
    justify-content: center !important;
    align-items: flex-start !important
}

.alert-top-right {
    justify-content: flex-end !important;
    align-items: flex-start !important
}

.alert-center-left {
    justify-content: flex-start !important;
    align-items: center !important
}

.alert-center {
    justify-content: center !important;
    align-items: center !important
}

.alert-center-right {
    justify-content: flex-end !important;
    align-items: center !important
}

.alert-bottom-left {
    justify-content: flex-start !important;
    align-items: flex-end !important
}

.alert-bottom {
    justify-content: center !important;
    align-items: flex-end !important
}

.alert-bottom-right {
    justify-content: flex-end !important;
    align-items: flex-end !important
}

.alert-backdrop .alert-container {
    position: relative;
    background: #fff;
    color: #000;
    padding: 25px 20px;
    margin: 10px;
    width: 50%;
    max-height: calc(100% - 5vw);
    overflow: visible
}

.alert-backdrop .alert-container .alert-heading {
    position: relative;
    text-align: center;
    margin: 10px 0 20px;
    font-size: 25px;
    font-weight: 800
}

.alert-backdrop .alert-container .alert-content {
    position: relative;
    text-align: left;
    margin: 0 5px 20px;
    font-size: 18px;
    font-weight: 700
}

.alert-backdrop .alert-container .alert-buttons-container {
    position: relative;
    display: flex;
    flex-direction: row-reverse;
    justify-content: space-between;
    align-items: center
}

.alert-backdrop .alert-container .alert-buttons-container button {
    position: relative;
    padding: 5px 7px;
    background: #2196f3;
    border: 2px solid #2196f3;
    color: #fff;
    border-radius: 5px;
    font-size: 15px;
    transition: .5s;
    font-weight: 600;
    box-shadow: rgba(255, 255, 255, .01) -2px -2px 6px, rgba(0, 0, 0, .8) 2px 2px 6px;
    cursor: pointer
}

.alert-backdrop .alert-container .alert-buttons-container button:active {
    box-shadow: rgba(255, 255, 255, .01) -2px -2px 6px inset, rgba(0, 0, 0, .8) 2px 2px 6px inset
}

.alert-backdrop .alert-container .alert-close {
    position: absolute;
    right: 0;
    margin: 0 20px;
    cursor: pointer;
    font-family: Arial, Helvetica, sans-serif !important;
    font-weight: 600;
    background-color: transparent;
    border: 0
}

.alert-backdrop .alert-container .alert-close:hover {
    color: #f03d30 !important
}

.alert-backdrop .alert-container svg.icon {
    position: relative;
    height: 70px;
    width: 70px;
    left: 50%;
    transform: translateX(-50%)
}

.alert-backdrop .alert-container textarea.alert-input {
    position: relative;
    width: 100%;
    margin: 0 0 20px;
    border: 2px solid #ccc;
    border-radius: 5px;
    padding: 5px;
    transition: .5s;
    font-weight: 700
}

.alert-backdrop .alert-container .alert-footer {
    position: relative;
    border-top: 2px solid #1e1e1e;
    margin: 25px 0 0;
    padding: 20px 0 0;
    text-align: center;
    font-weight: 700
}

.alert-backdrop .alert-container .alert-time-line {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 7px;
    background: #1e1e1e
}

@keyframes closeTimeLine {
    0% {
        width: 100%
    }

    100% {
        width: 0
    }
}

@keyframes zoom-in {
    0%, 25% {
        transform: scale(0)
    }

    50% {
        transform: scale(1.1)
    }

    75% {
        transform: scale(.9)
    }

    100% {
        transform: scale(1)
    }
}

@keyframes top {
    0%, 25% {
        transform: translateY(-200%)
    }

    50% {
        transform: translateY(10%)
    }

    75% {
        transform: translateY(-10%)
    }

    100% {
        transform: translateY(0)
    }
}

@keyframes bottom {
    0%, 25% {
        transform: translateY(200%)
    }

    50% {
        transform: translateY(-10%)
    }

    75% {
        transform: translateY(10%)
    }

    100% {
        transform: translateY(0)
    }
}

@keyframes left {
    0%, 25% {
        transform: translateX(-200%)
    }

    50% {
        transform: translateX(10%)
    }

    75% {
        transform: translateX(-10%)
    }

    100% {
        transform: translateX(0)
    }
}

@keyframes right {
    0%, 25% {
        transform: translateX(200%)
    }

    50% {
        transform: translateX(-10%)
    }

    75% {
        transform: translateX(10%)
    }

    100% {
        transform: translateX(0)
    }
}

@keyframes zoom-in-out {
    0%, 25% {
        transform: scale(1)
    }

    50% {
        transform: scale(.9)
    }

    75% {
        transform: scale(1.1)
    }

    100% {
        transform: scale(0)
    }
}

@keyframes top-out {
    0%, 25% {
        transform: translateY(0)
    }

    50% {
        transform: translateY(10%)
    }

    75% {
        transform: translateY(-10%)
    }

    100% {
        transform: translateY(200%)
    }
}

@keyframes bottom-out {
    0% {
        transform: translateY(0)
    }

    15% {
        transform: translateY(10%)
    }

    30% {
        transform: translateY(-10%)
    }

    100% {
        transform: translateY(200%)
    }
}

@keyframes left-out {
    0%, 25% {
        transform: translateX(0)
    }

    50% {
        transform: translateX(10%)
    }

    75% {
        transform: translateX(-10%)
    }

    100% {
        transform: translateX(200%)
    }
}

@keyframes right-out {
    0%, 25% {
        transform: translateX(0)
    }

    50% {
        transform: translateX(-10%)
    }

    75% {
        transform: translateX(10%)
    }

    100% {
        transform: translateX(-200%)
    }
}

.alert-toast-container ::-webkit-scrollbar {
    display: none
}

.alert-toast-container {
    display: flex;
    align-items: flex-end;
    bottom: 0;
    left: 0
}

.alert-toast-container .alert-toast {
    position: relative;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 15px;
    margin: 20px;
    min-width: 200px;
    background: #333;
    color: #fff;
    text-align: center;
    overflow: hidden
}

.alert-toast-container .alert-toast svg.icon {
    position: relative;
    height: 50px;
    width: 50px;
    margin: 0 10px 0 0
}

.alert-toast-container .alert-toast .alert-time-line {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 5px;
    background: rgba(255, 255, 255, .8)
}

@media (max-width:991px) {
    .alert-backdrop .alert-container {
        width: 60%
    }
}

@media (max-width:661px) {
    .alert-backdrop .alert-container {
        width: calc(100% - 10vw)
    }
}

@media (max-width:331px) {
    .alert-backdrop .alert-container {
        width: calc(100% - 5vw)
    }
}

.contextmenu {
    position: absolute;
    min-width: 160px;
    padding: 2px;
    margin: 0;
    border: 1px solid #bbb;
    background-image: #eee;
    background-image: -webkit-linear-gradient(to bottom, #fff 0, #e5e5e5 100px, #e5e5e5 100%);
    background-image: linear-gradient(to bottom, #fff 0, #e5e5e5 100px, #e5e5e5 100%);
    z-index: 20000000001;
    border-radius: 3px;
    box-shadow: 1px 1px 4px rgba(0, 0, 0, .2);
    opacity: 0;
    -webkit-transform: translate(0, 15px) scale(.95);
    transform: translate(0, 15px) scale(.95);
    transition: transform .1s ease-out, opacity .1s ease-out;
    pointer-events: none
}

.contextmenu-item {
    display: block;
    position: relative;
    margin: 0;
    padding: 0;
    white-space: nowrap
}

.contextmenu-btn {
    line-height: 8px;
    overflow: visible;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    display: flex;
    width: 100%;
    color: #444;
    font-family: Roboto, sans-serif;
    font-size: 12px;
    text-align: left;
    align-items: center;
    cursor: pointer;
    border: 1px solid transparent;
    white-space: nowrap;
    padding: 8px 8px;
    margin: 0 3px;
    border-radius: 3px;
    z-index: 999999
}

.contextmenu-btn::-moz-focus-inner {
    border: 0;
    padding: 0
}

.contextmenu-text {
    margin-left: 25px
}

.contextmenu-btn i {
    position: absolute;
    left: 8px;
    top: 50%;
    -webkit-transform: translateY(-50%);
    transform: translateY(-50%)
}

.contextmenu-hotkey {
    position: absolute;
    right: 5px;
    float: right;
    font-size: 12px;
    font-family: Acme;
    font-variant: all-small-caps
}

.contextmenu-item:hover>.contextmenu-btn {
    color: #fff;
    outline: 0;
    background-color: #2e3940;
    background-image: -webkit-linear-gradient(to bottom, #5d6d79, #2e3940);
    background-image: linear-gradient(to bottom, #5d6d79, #2e3940);
    border: 1px solid #2e3940
}

.contextmenu-item.disabled {
    opacity: .5;
    pointer-events: none
}

.contextmenu-item.disabled .contextmenu-btn {
    cursor: default
}

.contextmenu-separator {
    display: block;
    margin: 7px 5px;
    height: 0;
    border-bottom: 1px solid #aaa
}

.contextmenu-item.submenu::after {
    content: "";
    position: absolute;
    right: 6px;
    top: 50%;
    -webkit-transform: translateY(-50%);
    transform: translateY(-50%);
    border: 5px solid transparent;
    border-left-color: grey
}

.contextmenu-item.submenu:hover::after {
    border-left-color: #fff
}

.contextmenu-item:hover>.contextmenu, .show-contextmenu {
    opacity: 1;
    -webkit-transform: translate(0, 0) scale(1);
    transform: translate(0, 0) scale(1);
    pointer-events: auto
}

.contextmenu-item:hover>.contextmenu {
    -webkit-transition-delay: 0.1s;
    transition-delay: 0.3s
}

.contextmenu-i {
    display: inline-block;
    margin: 0;
    padding: 0;
    height: 16px;
    width: 16px;
    text-align: center;
    z-index: 1
}

